import MaximizingCharts from '../../../../assets/images/Maximizing_Charts.png';
import PropertyClipboard from '../../../../assets/images/Property_Clipboard.png';
import WorkingTogetherPuzzle from '../../../../assets/images/WorkingTogether_Puzzle.png';
import LiquidityWateringCan from '../../../../assets/images/Liquidity_WateringCan.png';

export const howWeDoIt = [
  {
    image: MaximizingCharts,
    title: 'Maximizing World-Class Innovations',
    text: 'Leveraging the latest technological capacities and systems, we are building our platform on the basis of blockchain technology, primarily on the Ethereum network. We will turn the real estate property into token offerings that will represent your ownership stake in a real world asset, one that is individual to you.',
  },
  {
    image: PropertyClipboard,
    title: 'Working Together',
    text: 'We do this by becoming partners with you. You join a limited partnership when investing into any property which is represented by the real estate tokens you purchase. These tokens limit your risk and provide users all the rewards that come with their investment.',
  },
  {
    image: WorkingTogetherPuzzle,
    title: 'Property Management',
    text: 'The real estate you invest in gets rented out and Dwella will take care of managing it all. Our users will gain all the benefits from the rental income without any of the obligations of a landlord.',
  },
  {
    image: LiquidityWateringCan,
    title: 'Liquidity',
    text: 'When it comes to real estate, buying and selling is a complicated process. By implementing a marketplace, you will be able to easily buy tokens and trade them with other Dwella users.',
  },
];
